#' COICOP Classification Codes
#'
#' @return It will return all the classified objects according to COICOP Classification
#' @export
#'
#' @examples
classification <- function(){
  url <- "https://stats.oecd.org/index.aspx?DataSetCode=PRICES_CPI#"

  read_1 <- rvest::read_html(url)

  filter_1 <- read_1 |> rvest::html_element(".outpoutcontainer") |>
    rvest::html_element(".PDim")

  nodes <- filter_1 |> rvest::html_elements("option") |> rvest::html_text2()

  codes <- ""

  codes_1 <- strsplit(gsub("[+]"," ",codes)," ")

  data_1 <- data.frame(names = nodes, code = codes_1[[1]])

  print(data_1)
}

